Test different values of the regularization parameter lambda and get to observe how it affects the bias-variance of regularized polynomial regression.
Lambda = 1


Lambda = 500


Lambda = 1000


Functions
Linear Regression Cost Function
function [J, grad] = linearRegCostFunction(X, y, theta, lambda)

    m = length(y); % number of training examples
    J = 0;
    grad = zeros(size(theta));

    h = X * theta;
    errors = h - y;
    J = sumsq(errors) / (2 * m);
    grad = (1 / m) * X' * errors;

    % Regularization
    J = J + (lambda / (2 * m)) * sumsq(theta(2:end));
    grad = grad + (lambda / m) * [0; theta(2:end)];

    grad = grad(:);
end
Train Linear Regression
function [theta] = trainLinearReg(X, y, lambda)

    % Initialize Theta
    initial_theta = zeros(size(X, 2), 1); 

    % Create "short hand" for the cost function to be minimized
    costFunction = @(t) linearRegCostFunction(X, y, t, lambda);

    % Now, costFunction is a function that takes in only one argument
    options = optimset('MaxIter', 200, 'GradObj', 'on');

    % Minimize using fmincg
    theta = fmincg(costFunction, initial_theta, options);
end
Plot Fit
function plotFit(min_x, max_x, mu, sigma, theta, p)

    hold on;

    % We plot a range slightly bigger than the min and max values to get
    % an idea of how the fit will vary outside the range of the data points
    x = (min_x - 15: 0.05 : max_x + 25)';

    % Map the X values 
    X_poly = polyFeatures(x, p);
    X_poly = bsxfun(@minus, X_poly, mu);
    X_poly = bsxfun(@rdivide, X_poly, sigma);

    % Add ones
    X_poly = [ones(size(x, 1), 1) X_poly];

    % Plot
    plot(x, X_poly * theta, '--', 'LineWidth', 2)

    % Hold off to the current figure
    hold off
end
Feature Normalize
function [X_norm, mu, sigma] = featureNormalize(X)

    mu = mean(X);
    X_norm = bsxfun(@minus, X, mu);

    sigma = std(X_norm);
    X_norm = bsxfun(@rdivide, X_norm, sigma);
end
Poly Features
function [X_poly] = polyFeatures(X, p)

    X_poly = zeros(numel(X), p);

    X_poly = X;
    for i=2:p
        X_poly = [X_poly X.^i];
    end;
end
Validation Curve
function [lambda_vec, error_train, error_val] = validationCurve(X, y, Xval, yval)

    % Selected values of lambda
    lambda_vec = [0 0.001 0.003 0.01 0.03 0.1 0.3 1 3 10]';

    error_train = zeros(length(lambda_vec), 1);
    error_val = zeros(length(lambda_vec), 1);

    for i = 1:length(lambda_vec)
        lambda = lambda_vec(i);
        theta = trainLinearReg(X, y, lambda);
        error_train(i) = linearRegCostFunction(X, y, theta, 0);
        error_val(i) = linearRegCostFunction(Xval, yval, theta, 0);
    end;
end
Learning Curve
    function [error_train, error_val] = learningCurve(X, y, Xval, yval, lambda)

        % Number of training examples
        m = size(X, 1);

        error_train = zeros(m, 1);
        error_val   = zeros(m, 1);

        for i = 1:m
            X_train = X(1:i, :);
            y_train = y(1:i);
            theta = trainLinearReg(X_train, y_train, lambda);
            error_train(i) = linearRegCostFunction(X_train, y_train, theta, 0);
            error_val(i) = linearRegCostFunction(Xval, yval, theta, 0);
        end;
    end
